import numpy as npimport os, rasterio, timedef calculate_indices():    for dirpath, dirnames, files in os.walk(os.path.join('.', 'data')):        extension = '10m.jp2'        for name in files:            if extension and name.lower().endswith(extension):                if 'b08' in name.lower().split('_'):                    b8 = os.path.join(dirpath, name)                elif 'b04' in name.lower().split('_'):                    b4 = os.path.join(dirpath, name)                elif 'b03' in name.lower().split('_'):                    b3 = os.path.join(dirpath, name)                elif 'b02' in name.lower().split('_'):                    b2 = os.path.join(dirpath, name)        print('Reading the downloaded bands.')    b2 = rasterio.open(b2)    b3 = rasterio.open(b3)    b4 = rasterio.open(b4)    b8 = rasterio.open(b8)        band_blue = b2.read()    band_green = b3.read()    band_red = b4.read()    band_nir = b8.read()        blue = band_blue.astype(float)    green = band_green.astype(float)    red = band_red.astype(float)    nir = band_nir.astype(float)        np.seterr(divide='ignore', invalid='ignore')        print('Calculating the indices.')    tic = time.perf_counter()    ndvi = (nir - red) / (nir + red)    ndwi = (green - nir) / (green + nir)    evi = 2.5*((nir - red)/(nir + (6*red) - (7.5*blue) + 1))    savi = 1.5*((nir - red) / (nir + red + 0.5))    toc = time.perf_counter()    print(f"Finished calculation in {toc - tic:0.4f} seconds")    